INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Mass Effect Trilogy', 4.8, 2012, 'Role-playing (RPG), Shooter', 'XBox, Playstation, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/1.png', 'Uma épica jornada espacial onde suas escolhas moldam o destino da galáxia.', 'A trilogia Mass Effect leva você ao comando do Comandante Shepard em uma luta desesperada contra uma ameaça alienígena ancestral. Combinando ação intensa, narrativa envolvente e decisões morais impactantes, cada escolha feita altera o rumo da história e o destino dos personagens ao seu redor.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Red Dead Redemption 2', 4.7, 2018, 'Role-playing (RPG), Adventure', 'XBox, Playstation, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/2.png', 'Um faroeste emocionante sobre honra, redenção e sobrevivência.', 'Red Dead Redemption mergulha o jogador em um Velho Oeste em transformação, onde foras da lei enfrentam um mundo moderno em ascensão. Na pele de John Marston, você embarca em uma jornada pessoal repleta de tiroteios, caçadas e dilemas morais que definem o fim da era dos pistoleiros.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('The Witcher 3: Wild Hunt', 4.7, 2014, 'Role-playing (RPG), Adventure', 'XBox, Playstation, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/3.png', ' Um RPG épico em um mundo sombrio e cheio de segredos.', 'Assuma o papel de Geralt de Rívia, um caçador de monstros profissional, em uma jornada por um continente devastado pela guerra e magia. The Witcher 3 oferece um vasto mundo aberto, narrativa densa, escolhas significativas e missões memoráveis que testam tanto sua espada quanto sua consciência.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Sekiro: Shadows Die Twice', 3.8, 2019, 'Role-playing (RPG), Adventure', 'XBox, Playstation, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/4.png', ' Ação brutal e precisão em um Japão feudal sombrio.', 'Em Sekiro, você é um guerreiro desonrado em busca de vingança e redenção. Misturando combate tenso com furtividade e exploração vertical, o jogo oferece uma experiência intensa com chefes desafiadores, ambientação imersiva e uma história sobre honra, perda e resiliência.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Ghost of Tsushima', 4.6, 2012, 'Role-playing (RPG), Adventure', 'XBox, Playstation, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/5.png', 'Lute como um samurai, torne-se um fantasma.', 'Ghost of Tsushima leva você à ilha de Tsushima durante a invasão mongol, onde Jin Sakai precisa escolher entre a honra dos samurais e as táticas furtivas de um guerreiro nas sombras. Com belos visuais, combates cinematográficos e um mundo aberto rico, é uma ode ao espírito do Japão feudal.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Super Mario World', 4.7, 1990, 'Platform', 'Super Ness, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/6.png', 'A clássica aventura do Mario em sua forma mais icônica.', 'Em Super Mario World, Mario e Luigi embarcam em uma jornada vibrante para salvar a Princesa Peach e derrotar Bowser. Com fases criativas, trilha sonora marcante e a introdução do querido dinossauro Yoshi, este clássico da era Super Nintendo é pura nostalgia e diversão atemporal.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Hollow Knight', 4.6, 2017, 'Platform', 'XBox, Playstation, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/7.png', ' Um metroidvania sombrio e deslumbrante.', 'Explore Hallownest, um reino subterrâneo repleto de mistérios, criaturas e desafios. Hollow Knight combina precisão em combate, exploração profunda e atmosfera melancólica, criando uma experiência envolvente e emocional para os fãs de jogos desafiadores e artísticos.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Ori and the Blind Forest', 4, 2015, 'Platform', 'XBox, Playstation, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/8.png', 'Uma fábula visualmente deslumbrante sobre perda e esperança.', 'Ori and the Blind Forest é uma aventura comovente em um mundo mágico onde a luz e a escuridão travam uma batalha eterna. Com arte pintada à mão, trilha sonora tocante e jogabilidade fluida, o jogo conta uma história sensível e inesquecível sobre coragem e sacrifício.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Cuphead', 4.6, 2017, 'Platform', 'XBox, Playstation, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/9.png', 'Tiros, chefes insanos e arte estilo anos 30.', 'Cuphead é um run-and-gun desafiador que combina mecânicas precisas com um estilo visual retrô, inspirado em desenhos animados dos anos 1930. Enfrente chefes únicos, domine padrões de ataque e mergulhe em um mundo animado com trilha sonora de jazz e animações desenhadas à mão.');
INSERT INTO tb_game (title, score, game_year, genre, platforms, img_url, short_description, long_description) VALUES ('Sonic CD', 4, 1993, 'Platform', 'Sega CD, PC', 'https://raw.githubusercontent.com/devsuperior/java-spring-dslist/main/resources/10.png', 'Velocidade, viagem no tempo e muita atitude.', 'Sonic CD é uma das aventuras mais criativas do ouriço azul, introduzindo mecânicas de viagem no tempo e novos personagens icônicos como Amy e Metal Sonic. Com trilha sonora marcante e design de fases dinâmico, é uma joia cult para fãs da era clássica do Sonic.');